var Resource = require('./resource');
var ComicVine = {};

ComicVine.RESOURCES_CODES = {
  characters: 4005,
  teams: 4060,
  publishers: 4010,
  concepts: 4015,
  issues: 4000,
  movies: 4025,
  episodes: 4070,
  story_arcs: 4045,
  creators: 4040,
  things: 4055,
  locations: 4020,
  volumes: 4050,
  series: 4075
}

ComicVine.Volume = {};

ComicVine.Volume.find = function(id) {
  return Resource.find({
    resourceName: 'volume',
    resourceCode: ComicVine.RESOURCES_CODES['volumes'],
    id: id
  });
};

ComicVine.Volume.search = function(name) {
  return Resource.filter({
    resourceName: 'volumes',
    where: {
      name: name
    }
  });
};

module.exports = ComicVine;