var Resource = require('./resource');
var Issue = {};

Issue.find = function(id) {
  return Resource.find({
    resourceName: 'issue',
    resourceCode: Resource.RESOURCES_CODES['issues'],
    id: id,
    field_list: ['id', 'name', 'issue_number', 'description', 'image']
  });
};

Issue.search = function(name) {
  return Resource.filter({
    resourceName: 'issues',
    where: {
      name: name
    }
  });
};

Issue.searchBy = function(filters) {
  return Resource.filter({
    resourceName: 'issues',
    where: filters
  });
};

module.exports = Issue;