var Resource = require('./resource');
var Publisher = {};

Publisher.find = function(id) {
  return Resource.find({
    resourceName: 'publisher',
    resourceCode: Resource.RESOURCES_CODES['publishers'],
    id: id,
    field_list: ['id', 'name', 'description', 'image']
  });
};

Publisher.search = function(name) {
  return Resource.filter({
    resourceName: 'publishers',
    where: {
      name: name
    }
  });
};

module.exports = Publisher;