var Resource = require('./resource');
var Volume = {};

Volume.find = function(id) {
  return Resource.find({
    resourceName: 'volume',
    resourceCode: Resource.RESOURCES_CODES['volumes'],
    id: id
  });
};

Volume.search = function(name) {
  return Resource.filter({
    resourceName: 'volumes',
    where: {
      name: name
    }
  });
};

module.exports = Volume;