var Resource = require('./resource');
var Vid = {};

Vid.Volume = {};

Vid.Volume.find = function(id) {
  return Resource.filter({
    resourceName: 'volumes',
    where: {
      id: id
    }
  }).then(function(resources) { return resources[0]; });
};

Vid.Volume.filter = function(name) {
  return Resource.filter({
    resourceName: 'volumes',
    where: {
      name: name
    }
  });
};

module.exports = Vid;